DECLARE @code_suff INT
DECLARE @code_post INT
USE AlfonsoDatabase;
DECLARE @section INT
DECLARE @item INT
SET @code_suff = 0
SET @code_post = 0
SET @section = 0
SET @item = 0

WHILE ( SELECT count(*) FROM MENU_SECTION) > @code_suff
BEGIN
   SELECT @section = min(id) FROM MENU_SECTION WHERE id > @section
   SET @code_suff = @code_suff + 1
   SET @code_post = 0
   SET @item = 0
   WHILE ( SELECT count(*) FROM MENU_ITEM WHERE section = @section) > @code_post
   BEGIN
	SELECT @item = min(id) FROM MENU_ITEM WHERE id > @item AND section = @section
	SET @code_post = @code_post + 1
	UPDATE MENU_ITEM SET code = CONCAT ( right('00' + cast(@code_suff as varchar(2)), 2), right('00' + cast(@code_post as varchar(2)), 2)) 
		WHERE id = @item
   END
END
